def test_grabber():
    card = OxfordDict(oxford_soup)
    print(card)
    assert card.word == oxford_card['word']
    assert card.pos == oxford_card['pos']
    assert card.pron == oxford_card['pron']


if '__main__' == __name__:
    test_grabber()
